$(function() {
  "use strict";
  $(function() {
    $("a[href*=#]").on("click", function(t) {
    //   t.preventDefault(),
        $("html, body").animate(
          { scrollTop: $($(this).attr("href")).offset().top },
          500,
          "linear"
        );
    });
  }),
    $(".navbar-default").waypoint("sticky", { offset: 30 }),
    $(".litebox-hero").magnificPopup({ type: "iframe" }),
    $(window).load(function() {
      var t = function() {
        $(".features div[class='row'] .col-md-7").each(function() {
          var t = 0,
            n = $(this);
          $.each(n.children(), function() {
            t += $(this).height();
          }),
            n.height(t);
        });
      };
      t(),
        $(window).on("resize", function() {
          t();
        });
    }),
    $("a[href*=#]:not([href=#])").on("click", function() {
      if (
        location.pathname.replace(/^\//, "") ===
          this.pathname.replace(/^\//, "") &&
        location.hostname === this.hostname
      ) {
        var t = $(this.hash);
        if ((t = t.length ? t : $("[name=" + this.hash.slice(1) + "]")).length)
          return (
            $("html,body").animate({ scrollTop: t.offset().top - 40 }, 1e3), !1
          );
      }
    }),
    $(".navbar-nav li a").on("click", function() {
      $(".navbar-collapse").collapse("hide");
    });
});
